arent;color:var(--text-primary);font-size:16px;font-weight:inherit}.dropdown:hover{background-color:var(--bg-secondary-hover)}.dropdown:hover .dropdown-btn{background-color:var(--bg-secondary-hover)}.dropdown-content{z-index:1;line-height:normal;overflow:hidden;position:absolute;display:none;min-width:160px;border:1px solid var(--border-color-hover);border-radius:0 0 10px 10px;background-color:#fff;box-shadow:0px 8px 16px var(--shadow)}.dropdown-content.show{display:block}.dropdown-content a{color:var(--text-secondary);font-size:16px;font-weight:400;text-decoration:none;display:block;padding:12px 16px;background-color:var(--bg-secondary)}.dropdown-content a:hover{background-color:var(--bg-secondary-hover)}.dropdown-icon{position:relative;bottom:-1px;font-size:19px;font-weight:400;display:inline-block;transform:rotateZ(90deg)}.card-controls{display:flex;align-items:center}.run-btn{cursor:pointer;color:var(--button-text);width:40px;height:40px;display:flex;align-items:center;justify-content:center;border:none;border-radius:50%;margin-left:10px;margin-right:10px;background-color:var(--button-bg);box-shadow:0 2px 5px var(--shadow)}.run-btn:hover{background-color:var(--button-bg-hover)}.run-btn:hover .loader{border-top-color:var(--button-bg-hover)}.run-btn span{display:none}.run-btn span.show{display:block}.run-btn .icon{font-size:20px;position:relative;top:-1px;left:2px}.run-btn .loader{position:absolute;width:10px;height:10px;border:5px solid var(--button-text);border-top:5px solid var(--button-bg);border-radius:50%;animation:spin 1s linear infinite}@keyframes spin{0%{transform:rotate(0deg)}100%{transform:rotate(360deg)}}.toggle-btn{color:var(--button-text);font-size:18px;border:none;border-radius:50%;width:24px;height:24px;display:flex;justify-content:center;align-items:center;cursor:pointer;position:absolute;bottom:10px;right:10px;background-color:var(--button-bg);box-shadow:0 2px 5px var(--shadow);transition:transform .3s ease}.toggle-btn.expanded:before{content:"−"}.toggle-btn:not(.expanded):before{content:"+"}.description{color:var(--text-secondary);font-size:12px;display:none;margin-top:5px}.description.show{display:block}.output{font-size:15px;color:var(--text-primary);display:none;margin-top:15px;padding-top:10px;border-top:1px solid var(--border-color);line-height:1.5;position:relative;overflow-y:hidden;transition:max-height .5s ease-out;max-height:1.5em}.output hr{margin:10px 0;border:none;border-bottom:1px solid var(--border-color)}.output.show{display:block}.output.expanded{white-space:normal;max-height:500px;transition:max-height .5s ease-in}.output.collapsing{max-height:1.5em;transition:max-height .3s ease-out}.output.collapsed .output-text{white-space:nowrap;overflow-x:hidden;text-overflow:ellipsis}.output-text{margin-right:50px}.output-text .engine{display:none}.output-text.multiple .engine{display:inline}.output-toggle{cursor:pointer;position:absolute;top:4px;right:16px;padding:5px}.output-toggle:after{display:inline-block;width:0;height:0;content:"";border-top:0;border-right:8px solid transparent;border-bottom:8px solid;border-left:8px solid transparent}.output.collapsed .output-toggle:after{display:inline-block;width:0;height:0;content:"";border-top:8px solid;border-bottom:0}#modal-image{display:none;position:fixed;top:0;left:0;width:calc(100% - 2*10px);height:100%;padding:10px;background:var(--bg-primary);justify-content:center;align-items:center;z-index:1000;transition:opacity 2s linear}#modal-image img{max-width:90%;max-height:75vh}#modal-image.show{display:flex}#modal-image .content{position:relative;display:flex;flex-direction:column;max-width:750px;border:solid 2px var(--border-color);border-radius:20px;overflow:hidden;align-items:center}#modal-image img{width:100%;max-width:100%}#modal-image .output{width:100%;margin:0;padding:10px}#modal-image .output-text{margin-left:10px}#modal-image .close{cursor:pointer;font-size:22px;color:var(--text-primary);position:absolute;top:10px;right:10px;width:30px;height: